    val query: String = "query Instrument(\$type: InstrumentType!, \$slug: String!, \$isBond: Boolean!, \$isEtf: Boolean!, \$isShare: Boolean!, \$bondSchoolQuestionId: ID!, \$hasBroker: Boolean!, \$hasIIS: Boolean!) {\n  tanker {\n    SEO: keyset(name: \"SEO\") {\n      data\n      id\n      \n    }\n    BubbleCases: keyset(name: \"BubbleCases\") {\n      data\n      id\n      \n    }\n    QnA: keyset(name: \"QnA\") {\n      data\n      id\n      \n    }\n    MoneyFundVTBM: keyset(name: \"MoneyFundVTBM\") {\n      id\n      data\n      \n    }\n    \n  }\n  bunker {\n    bubbleCases {\n      ofz {\n        key\n        theme\n        \n      }\n      vtbm {\n        key\n        theme\n        \n      }\n      \n    }\n    \n  }\n  ...Trade2\n  ...InstrumentBondData @include(if: \$isBond)\n  ...BondSchoolQuestionData @include(if: \$isBond)\n  ...InstrumentEtfData @include(if: \$isEtf)\n  ...InstrumentShareData @include(if: \$isShare)\n  news {\n    related(slug: \$slug) {\n      ...NewsRelatedProps\n      \n    }\n    \n  }\n  profile {\n    hasHistory: hasHistoryBySlug(slug: \$slug)\n    \n  }\n  instruments {\n    index {\n      ...InstrumentMainConditionsData\n      ...InstrumentMoneyFundCalculatorData\n      \n    }\n    metaData: bySlug(slug: \$slug, type: \$type) {\n      ... on EtfInstrumentItem {\n        isin\n        moneyFund {\n          profability {\n            percent\n            \n          }\n          \n        }\n        \n      }\n      ... on AnyInstrumentItem {\n        id\n        slug\n        logoId\n        type\n        ticker\n        taxFree\n        exchange\n        approved\n        description\n        displayName\n        commissionFree\n        issuer {\n          sector\n          country\n          description\n          displayName\n          type\n          \n        }\n        marketData {\n          id\n          price\n          priceStep\n          bidPrice\n          askPrice\n          timestamp\n          currencyCode\n          absoluteChange\n          percentChange\n          tradeOpen\n          sessionOpen\n          accruedInterest\n          nextSessionStateChange\n          noBrokerResult\n          lotSize\n          \n        }\n        \n      }\n      ... on ShareInstrumentItem {\n        isin\n        listingExchange\n        listingExchangeTicker\n        \n      }\n      ... on BondInstrumentItem {\n        isin\n        faceValue\n        maturityDate\n        issuer {\n          type\n          country\n          \n        }\n        bondInfo {\n          coupon {\n            fixedPeriod {\n              days\n              perYear\n              \n            }\n            payment {\n              toDate\n              nextDate\n              allTimePayment\n              payment {\n                amount\n                currencyCode\n                \n              }\n              \n            }\n            \n          }\n          \n        }\n        \n      }\n      ... on CurrencyInstrumentItem {\n        assetCurrencyCode\n        \n      }\n      \n    }\n    \n  }\n}\n\nfragment Trade2 on Query {\n  tanker {\n    Trade: keyset(name: \"Trade\") {\n      data\n      id\n      \n    }\n    \n  }\n  profile {\n    cards {\n      cardId\n      maskedNumber\n      paymentSystem\n      \n    }\n    \n  }\n  instruments {\n    metaData: bySlug(slug: \$slug, type: \$type) {\n      ... on AnyInstrumentItem {\n        id\n        slug\n        type\n        ticker\n        displayName\n        approved\n        exchange\n        commissionFree\n        marketData {\n          id\n          priceStep\n          bidPrice\n          askPrice\n          currencyCode\n          tradeOpen\n          sessionOpen\n          nextSessionStateChange\n          lotSize\n          \n        }\n        \n      }\n      ... on CurrencyInstrumentItem {\n        assetCurrencyCode\n        \n      }\n      \n    }\n    \n  }\n  ...PortfolioBalanceFragment\n  \n}\n\nfragment PortfolioBalanceFragment on Query {\n  brokerPortfolio: portfolio(agreementType: BROKER) @include(if: \$hasBroker) {\n    main {\n      currency {\n        id\n        amount\n        instrument {\n          id\n          slug\n          assetCurrencyCode\n          \n        }\n        \n      }\n      \n    }\n    position: positionBySlug(slug: \$slug) {\n      ... on AnyPortfolioPosition {\n        id\n        amount\n        \n      }\n      \n    }\n    \n  }\n  iisPortfolio: portfolio(agreementType: IIS) @include(if: \$hasIIS) {\n    main {\n      currency {\n        id\n        amount\n        instrument {\n          id\n          slug\n          assetCurrencyCode\n          \n        }\n        \n      }\n      \n    }\n    position: positionBySlug(slug: \$slug) {\n      ... on AnyPortfolioPosition {\n        id\n        amount\n        \n      }\n      \n    }\n    \n  }\n  \n}\n\nfragment InstrumentBondData on Query {\n  tanker {\n    InstrumentBond: keyset(name: \"InstrumentBond\") {\n      data\n      id\n      \n    }\n    \n  }\n  state {\n    ofAffairs {\n      now\n      refinancingRatePercent\n      \n    }\n    \n  }\n  instruments {\n    timeline: bondTimelineEventsBySlug(slug: \$slug) {\n      ... on TimelineEvent {\n        date\n        eventType\n        \n      }\n      ... on TimelineMaturityEvent {\n        maturity {\n          price {\n            amount\n            currencyCode\n            \n          }\n          \n        }\n        \n      }\n      ... on TimelineOfferEvent {\n        offer {\n          buyDate\n          endDate\n          beginDate\n          offerType\n          price {\n            amount\n            currencyCode\n            \n          }\n          \n        }\n        \n      }\n      ... on TimelineCouponEvent {\n        coupon {\n          rate\n          rateDate\n          payment {\n            amount\n            currencyCode\n            \n          }\n          \n        }\n        \n      }\n      ... on TimelineCouponRecurrentEvent {\n        recurrent {\n          dates\n          number\n          lastDate\n          \n        }\n        coupon {\n          rate\n          rateDate\n          couponPeriod {\n            days\n            perYear\n            \n          }\n          payment {\n            amount\n            currencyCode\n            \n          }\n          \n        }\n        \n      }\n      \n    }\n    metaData: bySlug(slug: \$slug, type: \$type) {\n      ... on AnyInstrumentItem {\n        id\n        slug\n        type\n        exchange\n        approved\n        displayName\n        commissionFree\n        issuer {\n          type\n          country\n          displayName\n          \n        }\n        marketData {\n          id\n          price\n          askPrice\n          timestamp\n          tradeOpen\n          sessionOpen\n          currencyCode\n          ownershipDate\n          accruedInterest\n          nextSessionStateChange\n          bondYield {\n            toDate\n            annualYield\n            allTimeYield\n            \n          }\n          \n        }\n        \n      }\n      ... on BondInstrumentItem {\n        faceValue\n        riskLevel\n        bondTaxType\n        maturityDate\n        riskLevel\n        bondRating {\n          agencyRatings {\n            agency\n            category\n            dateAward\n            rating\n            \n          }\n          \n        }\n        bondInfo {\n          coupon {\n            payment {\n              toDate\n              nextDate\n              allTimePayment\n              payment {\n                amount\n                currencyCode\n                \n              }\n              minPayment\n              fixedPayment\n              \n            }\n            fixedPeriod {\n              days\n              perYear\n              \n            }\n            \n          }\n          \n        }\n        \n      }\n      \n    }\n    \n  }\n  \n}\n\nfragment BondSchoolQuestionData on Query {\n  tanker {\n    QnA: keyset(name: \"QnA\") {\n      data\n      id\n      \n    }\n    \n  }\n  school {\n    questionById(id: \$bondSchoolQuestionId) {\n      id\n      \n    }\n    \n  }\n  \n}\n\nfragment InstrumentEtfData on Query {\n  tanker {\n    InstrumentEtf: keyset(name: \"InstrumentEtf\") {\n      data\n      id\n      \n    }\n    QnA: keyset(name: \"QnA\") {\n      data\n      id\n      \n    }\n    \n  }\n  bunker {\n    instruments {\n      etf {\n        etfcontentsoverrides {\n          id: slug\n          slug\n          contents {\n            id: name\n            name\n            weight\n            color\n            \n          }\n          \n        }\n        qna {\n          id\n          category\n          \n        }\n        \n      }\n      \n    }\n    \n  }\n  instruments {\n    metaData: bySlug(slug: \$slug, type: \$type) {\n      ... on AnyInstrumentItem {\n        id\n        slug\n        \n      }\n      ... on EtfInstrumentItem {\n        detailedEtfContents {\n          slug\n          type\n          name\n          weight\n          \n        }\n        \n      }\n      \n    }\n    \n  }\n  \n}\n\nfragment InstrumentShareData on Query {\n  tanker {\n    InstrumentShare: keyset(name: \"InstrumentShare\") {\n      data\n      id\n      \n    }\n    QnA: keyset(name: \"QnA\") {\n      data\n      id\n      \n    }\n    \n  }\n  bunker {\n    instruments {\n      share {\n        componentofqna {\n          id\n          category\n          \n        }\n        \n      }\n      \n    }\n    \n  }\n  instruments {\n    metaData: bySlug(slug: \$slug, type: \$type) {\n      ... on AnyInstrumentItem {\n        id\n        displayName\n        \n      }\n      ... on ShareInstrumentItem {\n        displayName\n        componentOf {\n          weight\n          instrument {\n            id\n            slug\n            displayName\n            logoId\n            \n          }\n          otherComponentNames\n          \n        }\n        \n      }\n      \n    }\n    \n  }\n  \n}\n\nfragment NewsRelatedProps on NewsRelated {\n  cursor\n  hasMore\n  items {\n    ...NewsItemProps\n    \n  }\n  \n}\n\nfragment NewsItemProps on NewsItem {\n  id\n  title\n  source\n  content\n  publicationDate\n  \n}\n\nfragment InstrumentMainConditionsData on InstrumentsIndex {\n  candles(slug: \"moexrepo\", limit: 1) {\n    close\n    \n  }\n  \n}\n\nfragment InstrumentMoneyFundCalculatorData on InstrumentsIndex {\n  candles(slug: \"moexrepo\", limit: 1) {\n    close\n    \n  }\n  \n}\n",

    @Json(name = "variables")
